package com.chatapp.server.Models;// Conversation.javaimport jakarta.persistence.*;        import lombok.Data;import java.time.LocalDateTime;@Entity@Table(name = "conversations")@Datapublic class Conversation {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JoinColumn(name = "agent_id")    private Agent agent;    @ManyToOne    @JoinColumn(name = "customer_id")    private Customer customer;    @Column(name = "created_at")    private LocalDateTime createdAt;    @PrePersist    protected void onCreate() {        createdAt = LocalDateTime.now();    }}