package com.chatapp.server.Models;// Message.javaimport jakarta.persistence.*;        import lombok.Data;import java.time.LocalDateTime;@Entity@Table(name = "messages")@Datapublic class Message {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JoinColumn(name = "conversation_id")    private Conversation conversation;    @ManyToOne    @JoinColumn(name = "sender")    private User sender;    @ManyToOne    @JoinColumn(name = "receiver")    private User receiver;    @Column(columnDefinition = "LONGTEXT")    private String content;    @Column(name = "created_at")    private LocalDateTime createdAt;    @PrePersist    protected void onCreate() {        createdAt = LocalDateTime.now();    }}