package com.chatapp.server.Models;import jakarta.persistence.*;        import lombok.Data;import java.time.LocalDateTime;@Entity@Table(name = "users")@Datapublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "first_name")    private String firstName;    @Column(name = "last_name")    private String lastName;    @Column(unique = true)    private String email;    private String password;    private String name;    private LocalDateTime birthdate;    @Enumerated(EnumType.STRING)    private UserType type;    @Column(name = "created_at")    private LocalDateTime createdAt;    @Column(name = "updated_at")    private LocalDateTime updatedAt;    @PrePersist    protected void onCreate() {        createdAt = LocalDateTime.now();        updatedAt = LocalDateTime.now();    }    @PreUpdate    protected void onUpdate() {        updatedAt = LocalDateTime.now();    }    public enum UserType {        CUSTOMER, AGENT    }}