package com.chatapp.server.dto;import com.chatapp.server.Models.Agent;import lombok.Data;@Datapublic class AgentDTO {    private Long id;    private UserDTO user;    public static AgentDTO fromEntity(Agent agent) {        AgentDTO dto = new AgentDTO();        dto.setId(agent.getId());        if (agent.getUser() != null) {            dto.setUser(UserDTO.fromEntity(agent.getUser()));        }        return dto;    }}