package com.chatapp.server.dto;// UserDTO.javaimport com.chatapp.server.Models.User;import lombok.Data;import java.time.LocalDateTime;@Datapublic class UserDTO {    private Long id;    private String firstName;    private String lastName;    private String email;    private String name;    private LocalDateTime birthdate;    private User.UserType type;    public static UserDTO fromEntity(User user) {        UserDTO dto = new UserDTO();        dto.setId(user.getId());        dto.setFirstName(user.getFirstName());        dto.setLastName(user.getLastName());        dto.setEmail(user.getEmail());        dto.setName(user.getName());        dto.setBirthdate(user.getBirthdate());        dto.setType(user.getType());        return dto;    }}