package com.chatapp.server.repository;// ConversationRepository.javaimport com.chatapp.server.Models.Conversation;import org.springframework.data.jpa.repository.JpaRepository;import java.util.List;import java.util.Optional;public interface ConversationRepository extends JpaRepository<Conversation, Long> {    List<Conversation> findByCustomerId(Long customerId);    List<Conversation> findByAgentId(Long agentId);    Optional<Conversation> findByCustomerIdAndAgentId(Long customerId, Long agentId);    List<Conversation> findByAgentIsNull();}