package com.chatapp.server.service;// UserService.javaimport com.chatapp.server.Models.User;import com.chatapp.server.repository.UserRepository;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.util.Optional;import java.util.List;@Service@RequiredArgsConstructorpublic class UserService {    private final UserRepository userRepository;    // Add this method to UserService.java    public List<User> findAll() {        return userRepository.findAll();    }    public Optional<User> findById(Long id) {        return userRepository.findById(id);    }    public User save(User user) {        return userRepository.save(user);    }    public boolean existsByEmail(String email) {        return userRepository.existsByEmail(email);    }    public Optional<User> findByEmail(String email) {        return userRepository.findByEmail(email);    }}